reader = PdfReader('Бадртдинова Алсу.pdf')

num_of_pages = len(reader.pages)
resumestr = ''
for page in range(num_of_pages):
    resumestr += reader.pages[page].extract_text()

from salary_counting import Count
c = Count(resumestr)

c.fill_req()
vacancy = c.agglomerate_clastering_for_mark()

agl_clastering_marks = [v['label'] for v in vacancy]

clastering_accuracy = accuracy_score(labels, agl_clastering_marks)
